````java
class MyShopAccountRefResolver implements AccountRefResolver {

Optional<AccountRef> resolve(Object source) {
    if (source instanceof InventoryShop is) {
        return AccountRef.of(AccountOwnerTypes.PLAYER, is.getOwnerUuid());
