    const clock = new MockClock(/*autoinstall =*/ true);
    gResolved.then(() => {
      clock.uninstall();
      done();
    });
  });

  describe('with clock mocked in beforeEach', () => {
    let clock: MockClock;
    beforeEach(() => {
      clock = new MockClock(/*autoinstall =*/ true);
    });
    afterEach(() => {
      clock.uninstall();
    });

    describe('sync blocks', () => {
      it('fails returning a GoogPromise', () => {
        return GoogPromise.resolve();
      });
    });

    describe('async blocks', () => {
      it('fails without await', async () => {});
      it('fails when goog promise resolved at end', async () => {
        await clock.tickAsync();
        await GoogPromise.resolve({foo: 'foo'});
      });
      it('fails when console.log without await at the end', async () => {
        await clock.tickAsync();
        console.log('wat?');
      });
    });
  });
});
