  def length; `self.length`; end
  alias size length
  alias bytelength length

  def each_char(&block)
    return enum_for(:each_char) { length } unless block_given?
    %x{
      for (var i = 0, length = self.length; i < length; i++) {
        #{yield `self.charAt(i)`};
      }
    }
    self
  end

  def each_byte(&block)
    return enum_for(:each_byte) { bytelength } unless block_given?
    %x{
      for (var i = 0, length = self.length; i < length; i++) {
        #{yield `self.charCodeAt(i)`};
      }
    }
    self
  end

  # Those don't need to be repeated:
  def bytes
    @bytes ||= each_byte.to_a
    @bytes.dup
  end

  def chars(&block)
    return each_char.to_a unless block

    each_char(&block)
  end
