public function decrypt($value)
{
    // these three set in settings or on the object
    $key = 'some random string of bits';
    $cipher = 'aes-256-gcm';
    $tagLength = 16;

    $ivLength = openssl_cipher_iv_length($cipher);

    $encrypted = base64_decode($value);

